import FunctionBar from "../components/FunctionBar.vue";
import IconTypeList from '../components/IconTypeList.vue';
export default {
  name: "Files",

  data() {
    return {
      listData: [],
      folderList: {},
      // 是否已经被创建
      // isCreated: false,
      // 排序方式
      sortType: "time",
      // 展示方式 icon table
      showType: "icon",
      // 搜索的文件夹
      searchFolder: []
    };
  },

  components: {
    FunctionBar,
    IconTypeList
  },
  methods: {
    // 请求文件列表
    async getListData() {
      if (this.$route.params.path.search("search") !== -1) return;
      let res = await this.$request(`/educenter/file/getCurDirFiles/${this.$store.state.userInfo.id}`, this.$route.params.path, "post", "params", "json");
      console.log(res, 63);

      if (res.data.success) {
        if (this.$store.state.sortType == "size") {
          res.data.data.files.sort((a, b) => {
            return a.size - b.size;
          });
        }

        this.listData = res.data.data.files;
        await this.getVideoList(res.data.data.files);
      } else {
        this.$message.error("获取文件列表失败,请刷新页面重试!");
      }
    },

    // 获取文件目录树
    async getFolderList(dir) {
      if (!dir) {
        console.log(81);
        let res = await this.$request("/educenter/dir/getUserDir", {
          id: this.$store.state.userInfo.id
        });

        if (res.data.data.dir == null) {
          this.$router.replace("/login");
          return;
        } // console.log(res);
        // console.log(JSON.parse(res.data.data.dir.memDir));


        this.folderList = JSON.parse(res.data.data.dir.memDir);
        this.$store.commit("updateFolderList", JSON.parse(res.data.data.dir.memDir));
        this.$store.commit("updateIsGetingFolder", false);
      } else {
        this.folderList = JSON.parse(dir);
        this.$store.commit("updateFolderList", JSON.parse(dir));
        this.$store.commit("updateIsGetingFolder", false);
      }
    }

  },

  async created() {
    this.$store.commit("updateCurrentParentId", 0);
    this.$axios.defaults.headers.common["Authorization"] = window.localStorage.getItem("token");
    this.$axios({
      url: "/user/file/list",
      method: "post",
      data: {
        id: this.$store.state.currentParentId
      }
    }).then(res => {
      console.log(res.data);

      if (res.data.success) {
        this.listData = res.data.files_list;
        this.searchFolder = res.data.folder_list;
      } else {
        this.$message.error("文件请求失败");
      }
    });
  },

  watch: {}
};
